const NOT_SUPPORTS_ERROR = 'Sorry, your browser does not support screen capture';

export class Recorder {
  constructor() {
    this.alertEl = document.getElementById('alert-message');
  }
  async start() {
    if (!this.checkSupportAndAlert()) {
      return;
    }

    this.hideAlert();

    if (this.recording) {
      window.URL.revokeObjectURL(this.recording);
    }

    this.chunks = [];
    this.recording = null;

    this.desktopStream = await this.getDesktopStream();
    if (this.desktopStream === null) {
      return;
    }
    this.desktopStream.addEventListener('inactive', (e) => {
      this.stop();
    });

    if (this.enableMic) {
      this.voiceStream = await this.getVoiceStream();
    }

    const tracks = [
      ...this.desktopStream.getVideoTracks(),
      ...this.mergeAudioStreams(this.desktopStream, this.voiceStream),
    ];
    this.active = true;
    this.stream = new MediaStream(tracks);

    // videoElement.srcObject = stream;
    // videoElement.muted = true;

    this.recorder = this.initMediaRecorder(this.stream);
    this.recorder.start(10);
  }

  stop() {
    this.alert.close();
    this.stopRecording();
  }

  stopRecording() {
    if (this.recorder == null) {
      return;
    }
    this.active = false;

    if (this.recorder.state !== 'inactive') {
      this.recorder.stop();
    }
    this.recorder = null;

    this.stopStream(this.stream);
    this.stopStream(this.voiceStream);
    this.stopStream(this.desktopStream);
    this.stream = null;
    this.voiceStream = null;
    this.desktopStream = null;

    this.recording = this.sanitizer.bypassSecurityTrustUrl(
      window.URL.createObjectURL(new Blob(this.chunks, { type: 'video/webm; codecs=vp8' }))
    );
  }

  stopStream(stream) {
    if (!stream) {
      return;
    }

    stream.getTracks().forEach((track) => {
      track.stop();
    });
  }

  async getDesktopStream() {
    try {
      const stream = await navigator.mediaDevices.getDisplayMedia({ video: true, audio: this.enableAudio });
      return stream;
    } catch (err) {
      if (this.isNotAllowedError(err)) {
        this.showAlert(ErrorAlert.NotAllowedScreen, true);
      } else {
        this.showAlert(ErrorAlert.Unknown, true);
      }
      return null;
    }
  }

  async getVoiceStream() {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: false, audio: this.enableMic });
      return stream;
    } catch (err) {
      if (this.isNotAllowedError(err)) {
        this.showAlert(ErrorAlert.NotAllowedMic, false);
      } else {
        this.showAlert(ErrorAlert.Unknown, true);
      }
      return null;
    }
  }

  initMediaRecorder(stream) {
    const recorder = new MediaRecorder(stream, {
      mimeType: 'video/webm',
    });

    recorder.addEventListener('dataavailable', (event) => {
      if (event.data && event.data.size > 0) {
        this.chunks.push(event.data);
      }
    });
    recorder.addEventListener('onstop', () => {
      this.stopRecording();
    });
    recorder.addEventListener('onerror', () => {
      this.showAlert(ErrorAlert.RecordingError, false);
      this.stopRecording();
    });

    return recorder;
  }

  mergeAudioStreams(desktopStream, voiceStream) {
    const context = new AudioContext();
    const destination = context.createMediaStreamDestination();
    let hasDesktop = false;
    let hasVoice = false;
    if (desktopStream && desktopStream.getAudioTracks().length > 0) {
      const source = context.createMediaStreamSource(desktopStream);
      source.connect(destination);
      hasDesktop = true;
    }

    if (voiceStream && voiceStream.getAudioTracks().length > 0) {
      const source = context.createMediaStreamSource(voiceStream);
      source.connect(destination);
      hasVoice = true;
    }

    return hasDesktop || hasVoice ? destination.stream.getAudioTracks() : [];
  }

  isNotAllowedError(err) {
    return err.name === 'NotAllowedError';
  }

  checkSupportAndAlert() {
    if (this.isSupports()) {
      return true;
    }

    this.showAlert(ErrorAlert.NotSupports, false);
    this.notSupports = true;

    return false;
  }

  isSupports() {
    if (navigator.mediaDevices?.getDisplayMedia === undefined) {
      return false;
    }

    return navigator.mediaDevices?.getUserMedia !== undefined;
  }

  showAlert(msg, hide) {
    this.alertEl.style.display = 'block';
    this.alertEl.innerText = msg;

    if (hide) {
      setTimeout(() => {
        this.hideAlert();
      }, 3000);
    }
  }

  hideAlert() {
    this.alertEl.style.display = 'none';
  }
}
